import React from "react";
import "./Courses.css";
import { useDispatch } from "react-redux";
const Courses = ({id,title,description,img,price}) => {
    
    console.log(title,"an");
    const dispatch=useDispatch();
  return (
    <div className="container m-10">
      <div className="row">
        <div className="col-sm-3">
          <div className="card cardd">
            <div className="card-header">
              <img  src={img} alt=""  height="200" width="200" className="imgg" />
            </div>
            <div className="card-body">
              <h3>{title}</h3>
              <p>{description}</p>
              <p>â‚¹{price}</p>
            </div>
            <div className="card-footer">
              <button className="btn btnn" onClick={()=>dispatch({type:"ADD",payload:{id:id,title:title,description:description,img:img,price:price}})}>Add to Cart</button>
            </div>
          </div>{/* card */}
          </div>
        </div>{/* row*/}
      </div>
  );
};

export default Courses;
