import React from 'react'
import { Provider, useSelector } from 'react-redux';
import './Cart.css'
import CartItem from './CartItem';
const Cart = () => {
  const cartData=useSelector(state=>state.cartReducer);


  const CartItem=()=>{ 
    return(
    cartData.map((itm)=>{
      return(
      <CartItem
            key={itm.id}
            title={itm.title}
            price={itm.price}
            // onRemove={.bind(null, itm)}
        />)
  }))
}
  return (
    <div>
      <CartItem/>
      <div className='total'>
        <span>Total Amount:</span>
